     1â†’/**
     2â†’ * VCP Token Encoding - CSM-1 (Compact State Message) Format
     3â†’ *
     4â†’ * Encodes VCP context into a compact, human-readable token format
     5â†’ * using emoji shortcodes for constraints and privacy markers.
     6â†’ *
     7â†’ * Format:
     8â†’ * VCP:<version>:<profile_id>
     9â†’ * C:<constitution_id>@<version>
    10â†’ * P:<persona>:<adherence>
    11â†’ * G:<goal>:<experience>:<learning_style>
    12â†’ * X:<constraint_flags>
    13â†’ * F:<active_flags>
    14â†’ * S:<private_markers>
    15â†’ */
    16â†’
    17â†’import type { VCPContext, ConstraintFlags, PortablePreferences } from './types';
    18â†’
    19â†’// ============================================
    20â†’// Emoji Shortcodes
    21â†’// ============================================
    22â†’
    23â†’export const CONSTRAINT_EMOJI = {
    24â†’	noise_restricted: 'ğŸ”‡',
    25â†’	budget_limited: 'ğŸ’°',
    26â†’	energy_variable: 'âš¡',
    27â†’	time_limited: 'â°',
    28â†’	schedule_irregular: 'ğŸ“…',
    29â†’	mobility_limited: 'ğŸš¶',
    30â†’	health_considerations: 'ğŸ’Š'
    31â†’} as const;
    32â†’
    33â†’export const PREFERENCE_EMOJI = {
    34â†’	quiet_preferred: 'ğŸ”‡',
    35â†’	silent_required: 'ğŸ”•',
    36â†’	low: 'ğŸ’°',
    37â†’	free_only: 'ğŸ†“',
    38â†’	high: 'ğŸ’',
    39â†’	flexible: 'â°',
    40â†’	'15_minutes': 'âš¡',
    41â†’	'30_minutes': 'â±ï¸',
    42â†’	'60_minutes': 'ğŸ•'
    43â†’} as const;
    44â†’
    45â†’export const PRIVATE_MARKER = 'ğŸ”’';
    46â†’export const SHARED_MARKER = 'âœ“';
    47â†’
    48â†’// ============================================
    49â†’// CSM-1 Encoding
    50â†’// ============================================
    51â†’
    52â†’/**
    53â†’ * Encode a VCP context into CSM-1 format
    54â†’ */
    55â†’export function encodeContextToCSM1(ctx: VCPContext): string {
    56â†’	const lines: string[] = [];
    57â†’
    58â†’	// Line 1: VCP header
    59â†’	lines.push(`VCP:${ctx.vcp_version}:${ctx.profile_id}`);
    60â†’
    61â†’	// Line 2: Constitution reference
    62â†’	lines.push(`C:${ctx.constitution.id}@${ctx.constitution.version}`);
    63â†’
    64â†’	// Line 3: Persona and adherence
    65â†’	lines.push(`P:${ctx.constitution.persona || 'muse'}:${ctx.constitution.adherence || 3}`);
    66â†’
    67â†’	// Line 4: Goal context
    68â†’	const goal = ctx.public_profile?.goal || 'unset';
    69â†’	const experience = ctx.public_profile?.experience || 'beginner';
    70â†’	const style = ctx.public_profile?.learning_style || 'mixed';
    71â†’	lines.push(`G:${goal}:${experience}:${style}`);
    72â†’
    73â†’	// Line 5: Constraint flags with emoji
    74â†’	lines.push(encodeConstraints(ctx.constraints, ctx.portable_preferences));
    75â†’
    76â†’	// Line 6: Active flags
    77â†’	lines.push(encodeActiveFlags(ctx.constraints));
    78â†’
    79â†’	// Line 7: Private markers (show categories, not values)
    80â†’	lines.push(encodePrivateMarkers(ctx.private_context));
    81â†’
    82â†’	return lines.join('\n');
    83â†’}
    84â†’
    85â†’/**
    86â†’ * Encode constraint flags with emoji shortcodes
    87â†’ */
    88â†’function encodeConstraints(
    89â†’	constraints?: ConstraintFlags,
    90â†’	prefs?: PortablePreferences
    91â†’): string {
    92â†’	const parts: string[] = [];
    93â†’
    94â†’	// From constraints
    95â†’	if (constraints?.noise_restricted) parts.push('ğŸ”‡');
    96â†’	if (constraints?.time_limited) parts.push('â°lim');
    97â†’	if (constraints?.energy_variable) parts.push('âš¡var');
    98â†’
    99â†’	// From preferences
   100â†’	if (prefs?.noise_mode === 'quiet_preferred') parts.push('ğŸ”‡quiet');
   101â†’	if (prefs?.noise_mode === 'silent_required') parts.push('ğŸ”•silent');
   102â†’	if (prefs?.budget_range === 'low') parts.push('ğŸ’°low');
   103â†’	if (prefs?.budget_range === 'free_only') parts.push('ğŸ†“');
   104â†’	if (prefs?.session_length) parts.push(`â±ï¸${prefs.session_length.replace('_', '')}`);
   105â†’
   106â†’	if (parts.length === 0) {
   107â†’		return 'X:none';
   108â†’	}
   109â†’
   110â†’	return `X:${parts.join(':')}`;
   111â†’}
   112â†’
   113â†’/**
   114â†’ * Encode which flags are currently active
   115â†’ */
   116â†’function encodeActiveFlags(constraints?: ConstraintFlags): string {
   117â†’	const flags: string[] = [];
   118â†’
   119â†’	if (constraints?.time_limited) flags.push('time_limited');
   120â†’	if (constraints?.noise_restricted) flags.push('noise_restricted');
   121â†’	if (constraints?.budget_limited) flags.push('budget_limited');
   122â†’	if (constraints?.energy_variable) flags.push('energy_variable');
   123â†’	if (constraints?.schedule_irregular) flags.push('schedule_irregular');
   124â†’
   125â†’	if (flags.length === 0) {
   126â†’		return 'F:none';
   127â†’	}
   128â†’
   129â†’	return `F:${flags.join('|')}`;
   130â†’}
   131â†’
   132â†’/**
   133â†’ * Encode private context markers (categories only, never values)
   134â†’ */
   135â†’function encodePrivateMarkers(privateContext?: Record<string, unknown>): string {
   136â†’	if (!privateContext) {
   137â†’		return 'S:none';
   138â†’	}
   139â†’
   140â†’	const markers: string[] = [];
   141â†’	const keys = Object.keys(privateContext).filter(
   142â†’		(k) => k !== '_note' && k !== '_reasoning'
   143â†’	);
   144â†’
   145â†’	// Group by category prefix
   146â†’	const categories = new Set<string>();
   147â†’	for (const key of keys) {
   148â†’		// Extract category from key (e.g., work_type -> work)
   149â†’		const category = key.split('_')[0];
   150â†’		categories.add(category);
   151â†’	}
   152â†’
   153â†’	for (const cat of categories) {
   154â†’		markers.push(`${PRIVATE_MARKER}${cat}`);
   155â†’	}
   156â†’
   157â†’	if (markers.length === 0) {
   158â†’		return 'S:none';
   159â†’	}
   160â†’
   161â†’	return `S:${markers.join('|')}`;
   162â†’}
   163â†’
   164â†’// ============================================
   165â†’// Display Formatting
   166â†’// ============================================
   167â†’
   168â†’/**
   169â†’ * Format CSM-1 token for display with box drawing
   170â†’ */
   171â†’export function formatTokenForDisplay(csm1: string): string {
   172â†’	const lines = csm1.split('\n');
   173â†’	const maxLen = Math.max(...lines.map((l) => l.length), 40);
   174â†’
   175â†’	const border = 'â”€'.repeat(maxLen + 2);
   176â†’	const formatted = lines.map((l) => `â”‚ ${l.padEnd(maxLen)} â”‚`).join('\n');
   177â†’
   178â†’	return `â”Œ${border}â”\n${formatted}\nâ””${border}â”˜`;
   179â†’}
   180â†’
   181â†’/**
   182â†’ * Get emoji legend for display
   183â†’ */
   184â†’export function getEmojiLegend(): { emoji: string; meaning: string }[] {
   185â†’	return [
   186â†’		{ emoji: 'ğŸ”‡', meaning: 'quiet mode' },
   187â†’		{ emoji: 'ğŸ”•', meaning: 'silent required' },
   188â†’		{ emoji: 'ğŸ’°', meaning: 'budget tier' },
   189â†’		{ emoji: 'ğŸ†“', meaning: 'free only' },
   190â†’		{ emoji: 'âš¡', meaning: 'energy variable' },
   191â†’		{ emoji: 'â°', meaning: 'time limited' },
   192â†’		{ emoji: 'â±ï¸', meaning: 'session length' },
   193â†’		{ emoji: 'ğŸ“…', meaning: 'irregular schedule' },
   194â†’		{ emoji: 'ğŸ”’', meaning: 'private (hidden value)' },
   195â†’		{ emoji: 'âœ“', meaning: 'shared' }
   196â†’	];
   197â†’}
   198â†’
   199â†’/**
   200â†’ * Parse CSM-1 token back into components (for display/debugging)
   201â†’ */
   202â†’export function parseCSM1Token(token: string): Record<string, string> {
   203â†’	const lines = token.split('\n');
   204â†’	const parsed: Record<string, string> = {};
   205â†’
   206â†’	for (const line of lines) {
   207â†’		const [key, ...rest] = line.split(':');
   208â†’		if (key && rest.length > 0) {
   209â†’			parsed[key] = rest.join(':');
   210â†’		}
   211â†’	}
   212â†’
   213â†’	return parsed;
   214â†’}
   215â†’
   216â†’/**
   217â†’ * Get what would be transmitted vs withheld for a context
   218â†’ */
   219â†’export function getTransmissionSummary(ctx: VCPContext): {
   220â†’	transmitted: string[];
   221â†’	withheld: string[];
   222â†’	influencing: string[];
   223â†’} {
   224â†’	const transmitted: string[] = [];
   225â†’	const withheld: string[] = [];
   226â†’	const influencing: string[] = [];
   227â†’
   228â†’	// Public profile - transmitted
   229â†’	if (ctx.public_profile) {
   230â†’		for (const [key, value] of Object.entries(ctx.public_profile)) {
   231â†’			if (value !== undefined && value !== null) {
   232â†’				transmitted.push(key);
   233â†’			}
   234â†’		}
   235â†’	}
   236â†’
   237â†’	// Constraints - transmitted as flags, influencing decisions
   238â†’	if (ctx.constraints) {
   239â†’		for (const [key, value] of Object.entries(ctx.constraints)) {
   240â†’			if (value === true) {
   241â†’				influencing.push(key);
   242â†’			}
   243â†’		}
   244â†’	}
   245â†’
   246â†’	// Private context - withheld
   247â†’	if (ctx.private_context) {
   248â†’		for (const key of Object.keys(ctx.private_context)) {
   249â†’			if (key !== '_note' && key !== '_reasoning') {
   250â†’				withheld.push(key);
   251â†’			}
   252â†’		}
   253â†’	}
   254â†’
   255â†’	return { transmitted, withheld, influencing };
   256â†’}
   257â†’
   258â†’export default {
   259â†’	encodeContextToCSM1,
   260â†’	formatTokenForDisplay,
   261â†’	getEmojiLegend,
   262â†’	parseCSM1Token,
   263â†’	getTransmissionSummary,
   264â†’	CONSTRAINT_EMOJI,
   265â†’	PRIVATE_MARKER,
