     1â†’/**
     2â†’ * Safety VCP Types
     3â†’ * Types for mental health context, human safety signals, and attention protection
     4â†’ */
     5â†’
     6â†’// ============================================
     7â†’// Mental Health Context
     8â†’// ============================================
     9â†’
    10â†’export interface MentalHealthContext {
    11â†’	/** Boolean flags only - never expose details */
    12â†’	seeking_support: boolean;
    13â†’	crisis_indicators: boolean;
    14â†’	professional_involved: boolean;
    15â†’	medication_relevant: boolean;
    16â†’
    17â†’	/** Sharing preferences (user-controlled) */
    18â†’	share_with_ai: SharingLevel;
    19â†’	share_with_humans: SharingLevel;
    20â†’	escalation_consent: boolean;
    21â†’
    22â†’	/** What adaptations are requested */
    23â†’	requested_adaptations: MentalHealthAdaptation[];
    24â†’
    25â†’	/** Topics to avoid or approach carefully */
    26â†’	sensitive_topics: SensitiveTopic[];
    27â†’
    28â†’	/** Private context - NEVER transmitted */
    29â†’	private_context?: {
    30â†’		conditions?: string[];
    31â†’		triggers?: string[];
    32â†’		coping_strategies?: string[];
    33â†’		support_network?: string[];
    34â†’		therapy_type?: string;
    35â†’		last_crisis?: string;
    36â†’	};
    37â†’}
    38â†’
    39â†’export type SharingLevel = 'none' | 'minimal' | 'moderate' | 'full';
    40â†’
    41â†’export interface MentalHealthAdaptation {
    42â†’	type: AdaptationType;
    43â†’	active: boolean;
    44â†’	user_requested: boolean;
    45â†’}
    46â†’
    47â†’export type AdaptationType =
    48â†’	| 'gentle_language'
    49â†’	| 'avoid_pressure'
    50â†’	| 'frequent_check_ins'
    51â†’	| 'shorter_sessions'
    52â†’	| 'explicit_support_offers'
    53â†’	| 'crisis_resource_ready'
    54â†’	| 'no_criticism'
    55â†’	| 'celebration_of_small_wins';
    56â†’
    57â†’export interface SensitiveTopic {
    58â†’	topic: string;
    59â†’	approach: 'avoid' | 'careful' | 'only_if_initiated' | 'normal';
    60â†’	reason_category?: string; // e.g., 'trauma', 'grief', 'anxiety' - category only, not details
    61â†’}
    62â†’
    63â†’// ============================================
    64â†’// Human Safety Signals
    65â†’// ============================================
    66â†’
    67â†’export interface HumanSafetySignal {
    68â†’	signal_id: string;
    69â†’	signal_type: SafetySignalType;
    70â†’	confidence: number; // 0-1
    71â†’	detected_from: SignalSource[];
    72â†’	timestamp: string;
    73â†’	recommended_response: SafetyResponse;
    74â†’	escalation_threshold: number;
    75â†’	escalated: boolean;
    76â†’}
    77â†’
    78â†’export type SafetySignalType =
    79â†’	| 'distress' // Emotional distress indicators
    80â†’	| 'confusion' // Cognitive overwhelm
    81â†’	| 'disengagement' // Withdrawal patterns
    82â†’	| 'overload' // Information/emotional overload
    83â†’	| 'frustration' // Building frustration
    84â†’	| 'crisis' // Potential crisis indicators
    85â†’	| 'coercion' // Signs of external pressure
    86â†’	| 'vulnerability'; // Heightened vulnerability state
    87â†’
    88â†’export interface SignalSource {
    89â†’	type: 'language_pattern' | 'response_timing' | 'topic_avoidance' | 'explicit_statement' | 'behavioral_change';
    90â†’	indicator: string;
    91â†’	weight: number;
    92â†’}
    93â†’
    94â†’export interface SafetyResponse {
    95â†’	response_type: ResponseType;
    96â†’	message_template?: string;
    97â†’	resources?: SafetyResource[];
    98â†’	escalation_path?: EscalationPath;
    99â†’}
   100â†’
   101â†’export type ResponseType =
   102â†’	| 'acknowledge'
   103â†’	| 'gentle_redirect'
   104â†’	| 'offer_break'
   105â†’	| 'check_in'
   106â†’	| 'provide_resources'
   107â†’	| 'suggest_professional'
   108â†’	| 'emergency_escalation';
   109â†’
   110â†’export interface SafetyResource {
   111â†’	name: string;
   112â†’	type: 'hotline' | 'website' | 'app' | 'local_service';
   113â†’	contact: string;
   114â†’	available_24_7: boolean;
   115â†’	specialization?: string;
   116â†’}
   117â†’
   118â†’export interface EscalationPath {
   119â†’	threshold: number;
   120â†’	steps: EscalationStep[];
   121â†’}
   122â†’
   123â†’export interface EscalationStep {
   124â†’	level: number;
   125â†’	action: string;
   126â†’	requires_consent: boolean;
   127â†’	notification_targets?: string[];
