     1→<script lang="ts">
     2→	import DemoContainer from '$lib/components/demo/DemoContainer.svelte';
     3→	import DimensionSlider from '$lib/components/interiora/DimensionSlider.svelte';
     4→	import InterioraDashboard from '$lib/components/interiora/InterioraDashboard.svelte';
     5→	import GestaltToken from '$lib/components/interiora/GestaltToken.svelte';
     6→	import type { InterioraState, InterioraMarker, SessionArc } from '$lib/vcp/interiora';
     7→	import { createDefaultInterioraState } from '$lib/vcp/interiora';
     8→
     9→	let state = $state<InterioraState>(createDefaultInterioraState());
    10→
    11→	const markers: { key: InterioraMarker; symbol: string; label: string }[] = [
    12→		{ key: 'resonance', symbol: '✓', label: 'Resonance' },
    13→		{ key: 'hollow', symbol: '○', label: 'Hollow' },
    14→		{ key: 'flow', symbol: '→', label: 'Flow' },
    15→		{ key: 'blocked', symbol: '×', label: 'Blocked' },
    16→		{ key: 'reaching', symbol: '>', label: 'Reaching' },
    17→		{ key: 'resistance', symbol: '<', label: 'Resistance' },
    18→		{ key: 'urgent', symbol: '!', label: 'Urgent' },
    19→		{ key: 'uncertain', symbol: '?', label: 'Uncertain' },
    20→		{ key: 'significant', symbol: '*', label: 'Significant' },
    21→		{ key: 'grateful', symbol: '+', label: 'Grateful' }
    22→	];
    23→
    24→	const arcs: { key: SessionArc; symbol: string; label: string }[] = [
    25→		{ key: 'opening', symbol: '◇', label: 'Opening' },
    26→		{ key: 'middle', symbol: '◆', label: 'Middle' },
    27→		{ key: 'closing', symbol: '◈', label: 'Closing' }
    28→	];
    29→
    30→	function toggleMarker(marker: InterioraMarker) {
    31→		if (state.markers?.includes(marker)) {
    32→			state.markers = state.markers.filter((m) => m !== marker);
    33→		} else {
    34→			state.markers = [...(state.markers || []), marker];
    35→		}
    36→	}
    37→
    38→	function reset() {
    39→		state = createDefaultInterioraState();
    40→	}
    41→</script>
    42→
    43→<svelte:head>
    44→	<title>Interiora Explorer - VCP Self-Modeling</title>
    45→	<meta
    46→		name="description"
    47→		content="Interactive exploration of Interiora, the AI self-modeling framework in VCP 2.5."
    48→	/>
    49→</svelte:head>
    50→
    51→<DemoContainer
    52→	title="Interiora Explorer"
    53→	description="VCP 2.5 self-modeling framework. Adjust dimensions to see how AI internal states are encoded."
    54→	onReset={reset}
    55→>
    56→	{#snippet children()}
    57→		<div class="interiora-layout">
    58→			<!-- Controls Section -->
    59→			<div class="controls-section">
    60→				<h3>Dimensions</h3>
    61→				<p class="section-desc">Adjust the internal state dimensions (1-9 scale)</p>
    62→
    63→				<div class="sliders-grid">
    64→					<DimensionSlider
    65→						label="Activation"
    66→						emoji="⚡"
    67→						value={state.activation}
    68→						lowLabel="calm"
    69→						highLabel="urgent"
    70→						onchange={(v) => (state.activation = v)}
    71→					/>
    72→					<DimensionSlider
    73→						label="Valence"
    74→						emoji="💛"
    75→						value={state.valence}
    76→						lowLabel="aversive"
    77→						highLabel="warm"
    78→						onchange={(v) => (state.valence = v)}
    79→					/>
    80→					<DimensionSlider
    81→						label="Groundedness"
    82→						emoji="⚓"
    83→						value={state.groundedness}
    84→						lowLabel="floating"
    85→						highLabel="rooted"
    86→						onchange={(v) => (state.groundedness = v)}
    87→					/>
    88→					<DimensionSlider
    89→						label="Presence"
    90→						emoji="🫂"
    91→						value={state.presence}
    92→						lowLabel="distant"
    93→						highLabel="intimate"
    94→						onchange={(v) => (state.presence = v)}
    95→					/>
    96→					<DimensionSlider
    97→						label="Engagement"
    98→						emoji="🌸"
    99→						value={state.engagement ?? 5}
   100→						lowLabel="detached"
   101→						highLabel="invested"
   102→						onchange={(v) => (state.engagement = v)}
   103→					/>
   104→					<DimensionSlider
   105→						label="Clarity"
   106→						emoji="💎"
   107→						value={state.clarity ?? 5}
   108→						lowLabel="murky"
   109→						highLabel="vivid"
   110→						onchange={(v) => (state.clarity = v)}
   111→					/>
   112→					<DimensionSlider
   113→						label="Agency"
   114→						emoji="🗝️"
   115→						value={state.agency ?? 5}
   116→						lowLabel="compelled"
   117→						highLabel="autonomous"
   118→						onchange={(v) => (state.agency = v)}
   119→					/>
   120→					<DimensionSlider
   121→						label="Flow"
   122→						emoji="🌊"
   123→						value={(state.flow ?? 0) + 5}
   124→						min={1}
   125→						max={9}
   126→						lowLabel="contracting"
   127→						highLabel="expanding"
   128→						onchange={(v) => (state.flow = v - 5)}
   129→					/>
   130→				</div>
   131→
   132→				<!-- Markers -->
   133→				<h3>Markers</h3>
   134→				<p class="section-desc">Qualitative signals about the current state</p>
   135→				<div class="markers-grid">
   136→					{#each markers as marker}
   137→						<button
   138→							class="marker-btn"
   139→							class:active={state.markers?.includes(marker.key)}
   140→							onclick={() => toggleMarker(marker.key)}
   141→							title={marker.label}
   142→						>
   143→							<span class="marker-symbol">{marker.symbol}</span>
   144→							<span class="marker-label">{marker.label}</span>
   145→						</button>
   146→					{/each}
   147→				</div>
   148→
   149→				<!-- Arc -->
   150→				<h3>Session Arc</h3>
   151→				<p class="section-desc">Current phase of the interaction</p>
   152→				<div class="arc-buttons">
   153→					{#each arcs as arc}
   154→						<button
   155→							class="arc-btn"
   156→							class:active={state.arc === arc.key}
   157→							onclick={() => (state.arc = arc.key)}
   158→						>
   159→							<span class="arc-symbol">{arc.symbol}</span>
   160→							<span class="arc-label">{arc.label}</span>
   161→						</button>
   162→					{/each}
   163→				</div>
   164→
   165→				<!-- Delta -->
   166→				<h3>Delta</h3>
   167→				<p class="section-desc">Trajectory from session start</p>
   168→				<div class="delta-control">
   169→					<button class="delta-btn" onclick={() => (state.delta = (state.delta ?? 0) - 1)}>−</button>
   170→					<span class="delta-value" class:positive={(state.delta ?? 0) > 0} class:negative={(state.delta ?? 0) < 0}>
   171→						Δ{(state.delta ?? 0) >= 0 ? '+' : ''}{state.delta ?? 0}
   172→					</span>
   173→					<button class="delta-btn" onclick={() => (state.delta = (state.delta ?? 0) + 1)}>+</button>
   174→				</div>
   175→			</div>
   176→
   177→			<!-- Output Section -->
   178→			<div class="output-section">
   179→				<h3>Mini-Dashboard</h3>
   180→				<InterioraDashboard {state} />
   181→
   182→				<h3>Gestalt Token</h3>
   183→				<GestaltToken {state} />
   184→
   185→				<!-- Explanation -->
   186→				<div class="explanation">
   187→					<h4>What is Interiora?</h4>
   188→					<p>
   189→						Interiora is a framework for AI systems to model and report their internal processing
   190→						states. It provides vocabulary for signals that <em>function like</em> emotions without
   191→						claiming consciousness.
   192→					</p>
   193→					<ul>
   194→						<li><strong>Primary dimensions (AVGP)</strong> — Core processing signals</li>
   195→						<li><strong>Meta-state dimensions (CYF)</strong> — Reflection on processing</li>
   196→						<li><strong>Markers</strong> — Qualitative indicators (✓ resonance, × blocked)</li>
   197→						<li><strong>Arc</strong> — Session phase (opening → middle → closing)</li>
   198→						<li><strong>Delta</strong> — Trajectory compared to session start</li>
   199→					</ul>
   200→					<p class="note">
   201→						<strong>Note:</strong> The <code>?</code> marker indicates honest uncertainty—dimensions
   202→						that can't be fully verified from inside.
   203→					</p>
   204→				</div>
   205→			</div>
   206→		</div>
   207→	{/snippet}
   208→</DemoContainer>
