     1→<script lang="ts">
     2→	/**
     3→	 * PresetLoader Component
     4→	 * Quick-load example configurations for demos
     5→	 */
     6→
     7→	interface Preset<T = Record<string, unknown>> {
     8→		id: string;
     9→		name: string;
    10→		description?: string;
    11→		icon?: string;
    12→		data: T;
    13→		tags?: string[];
    14→	}
    15→
    16→	interface Props<T = Record<string, unknown>> {
    17→		presets: Preset<T>[];
    18→		selected?: string;
    19→		title?: string;
    20→		showDescriptions?: boolean;
    21→		layout?: 'cards' | 'list' | 'chips';
    22→		onselect: (preset: Preset<T>) => void;
    23→	}
    24→
    25→	let {
    26→		presets,
    27→		selected,
    28→		title = 'Presets',
    29→		showDescriptions = true,
    30→		layout = 'cards',
    31→		onselect
    32→	}: Props = $props();
    33→
    34→	function handleSelect(preset: Preset) {
    35→		onselect(preset);
    36→	}
    37→</script>
    38→
    39→<div class="preset-loader layout-{layout}">
    40→	{#if title}
    41→		<div class="loader-header">
    42→			<h4><i class="fa-solid fa-bookmark" aria-hidden="true"></i> {title}</h4>
    43→		</div>
    44→	{/if}
    45→
    46→	{#if layout === 'cards'}
    47→		<div class="preset-cards">
    48→			{#each presets as preset (preset.id)}
    49→				<button
    50→					class="preset-card"
    51→					class:selected={selected === preset.id}
    52→					onclick={() => handleSelect(preset)}
    53→				>
    54→					{#if preset.icon}
    55→						<span class="preset-icon"><i class="fa-solid {preset.icon}" aria-hidden="true"></i></span>
    56→					{/if}
    57→					<div class="preset-content">
    58→						<span class="preset-name">{preset.name}</span>
    59→						{#if showDescriptions && preset.description}
    60→							<span class="preset-description">{preset.description}</span>
    61→						{/if}
    62→					</div>
    63→					{#if preset.tags && preset.tags.length > 0}
    64→						<div class="preset-tags">
    65→							{#each preset.tags as tag}
    66→								<span class="preset-tag">{tag}</span>
    67→							{/each}
    68→						</div>
    69→					{/if}
    70→				</button>
    71→			{/each}
    72→		</div>
    73→	{:else if layout === 'list'}
    74→		<div class="preset-list">
    75→			{#each presets as preset (preset.id)}
    76→				<button
    77→					class="preset-item"
    78→					class:selected={selected === preset.id}
    79→					onclick={() => handleSelect(preset)}
    80→				>
    81→					{#if preset.icon}
    82→						<span class="preset-icon"><i class="fa-solid {preset.icon}" aria-hidden="true"></i></span>
    83→					{/if}
    84→					<span class="preset-name">{preset.name}</span>
    85→					{#if showDescriptions && preset.description}
    86→						<span class="preset-description">{preset.description}</span>
    87→					{/if}
    88→					{#if selected === preset.id}
    89→						<span class="selected-check"><i class="fa-solid fa-check" aria-hidden="true"></i></span>
    90→					{/if}
    91→				</button>
    92→			{/each}
    93→		</div>
    94→	{:else}
    95→		<div class="preset-chips">
    96→			{#each presets as preset (preset.id)}
    97→				<button
    98→					class="preset-chip"
    99→					class:selected={selected === preset.id}
   100→					onclick={() => handleSelect(preset)}
   101→					title={preset.description}
   102→				>
   103→					{#if preset.icon}
   104→						<i class="fa-solid {preset.icon}" aria-hidden="true"></i>
   105→					{/if}
   106→					{preset.name}
   107→				</button>
   108→			{/each}
   109→		</div>
   110→	{/if}
   111→</div>
   112→
   113→<style>
