     1→<script lang="ts">
     2→	/**
     3→	 * ContrastView Component
     4→	 * Side-by-side comparison of VCP contexts showing different outcomes
     5→	 */
     6→
     7→	interface ContrastItem {
     8→		id: string;
     9→		name: string;
    10→		description?: string;
    11→		context: Record<string, unknown>;
    12→		outcome?: string;
    13→		highlights?: string[];
    14→	}
    15→
    16→	interface Props {
    17→		items: ContrastItem[];
    18→		title?: string;
    19→		showOutcomes?: boolean;
    20→		columns?: 2 | 3;
    21→		baseline?: ContrastItem;
    22→	}
    23→
    24→	let {
    25→		items,
    26→		title = 'Context Comparison',
    27→		showOutcomes = true,
    28→		columns = 2,
    29→		baseline
    30→	}: Props = $props();
    31→
    32→	let selectedItems = $state<string[]>(items.slice(0, columns).map((i) => i.id));
    33→	let showBaseline = $state(false);
    34→
    35→	function toggleItem(id: string) {
    36→		if (selectedItems.includes(id)) {
    37→			if (selectedItems.length > 1) {
    38→				selectedItems = selectedItems.filter((i) => i !== id);
    39→			}
    40→		} else {
    41→			if (selectedItems.length >= columns) {
    42→				selectedItems = [...selectedItems.slice(1), id];
    43→			} else {
    44→				selectedItems = [...selectedItems, id];
    45→			}
    46→		}
    47→	}
    48→
    49→	const displayedItems = $derived(
    50→		showBaseline && baseline
    51→			? [baseline, ...items.filter((i) => selectedItems.includes(i.id))]
    52→			: items.filter((i) => selectedItems.includes(i.id))
    53→	);
    54→</script>
