    72→
    73→	let selectedBelief = $state<BeliefState | null>(null);
    74→
    75→	function getConfidenceColor(confidence: number): string {
    76→		if (confidence >= 0.8) return '#2ecc71';
    77→		if (confidence >= 0.5) return '#f39c12';
    78→		return '#e74c3c';
    79→	}
    80→
    81→	function getCalibrationScore(belief: BeliefState): number {
    82→		if (belief.calibration_history.length === 0) return 0;
    83→		const avgDivergence =
    84→			belief.calibration_history.reduce((sum, c) => sum + c.divergence, 0) /
    85→			belief.calibration_history.length;
    86→		return Math.max(0, 1 - avgDivergence);
    87→	}
    88→
    89→	function reset() {
