     1→<script lang="ts">
     2→	/**
     3→	 * AuditPanel Component
     4→	 * Shows what was shared, withheld, and how it influenced outcomes
     5→	 */
     6→
     7→	interface AuditEntry {
     8→		field: string;
     9→		category: 'shared' | 'withheld' | 'influenced';
    10→		value?: string | number | boolean;
    11→		reason?: string;
    12→		stakeholder?: string;
    13→		timestamp?: string;
    14→	}
    15→
    16→	interface Props {
    17→		entries: AuditEntry[];
    18→		title?: string;
    19→		showTimestamps?: boolean;
    20→		groupByCategory?: boolean;
    21→		compact?: boolean;
    22→	}
    23→
    24→	let {
    25→		entries,
    26→		title = 'Audit Trail',
    27→		showTimestamps = false,
    28→		groupByCategory = true,
    29→		compact = false
    30→	}: Props = $props();
    31→
    32→	const groupedEntries = $derived.by(() => {
    33→		if (!groupByCategory) return { all: entries };
    34→
    35→		return {
    36→			shared: entries.filter((e) => e.category === 'shared'),
    37→			influenced: entries.filter((e) => e.category === 'influenced'),
    38→			withheld: entries.filter((e) => e.category === 'withheld')
    39→		};
    40→	});
    41→
    42→	const categories = [
    43→		{ key: 'shared', label: 'Shared', icon: 'fa-circle-check', color: 'success' },
    44→		{ key: 'influenced', label: 'Influenced', icon: 'fa-bolt', color: 'warning' },
    45→		{ key: 'withheld', label: 'Withheld', icon: 'fa-lock', color: 'danger' }
    46→	] as const;
    47→
    48→	function formatValue(value: string | number | boolean | undefined): string {
    49→		if (value === undefined) return '—';
    50→		if (typeof value === 'boolean') return value ? 'Yes' : 'No';
    51→		return String(value);
    52→	}
    53→</script>
